    private static final String LETTER_PATTERN = "^[a-zA-Z]+$";
    private static final String PHONE_PATTERN = "^\\d{10}$";

    /**
     * Validates if the given string that represents email is not null and not empty and respects the standards for a correct email.
     * @param mail represents input the string to validate
     * @return true if the string is valid, false otherwise
     */
    public static boolean validateMail(String mail) {
        Pattern pattern = Pattern.compile(EMAIL_PATTERN);
        if (!pattern.matcher(mail).matches()) {
            return false;
        }

        return true;
    }

    /**
     * Validates if the text field has just letters
     * @param textField represents the input the text field to validate
     * @return true if the text field number is valid, false otherwise
     */
    public static boolean validateFieldLetter(JTextField textField) {
        Pattern pattern = Pattern.compile(LETTER_PATTERN);
        if (!pattern.matcher(textField.getText()).matches()) {
            return false;
        }
      return true;
    }

    /**
     * Validates if the text field for phone number respects the standards for phone numbers (exactly 10 digits)
     * @param textField represents the input the text field to validate
     * @return true if the phone number is valid, false otherwise
     */
    public static boolean validateFieldPhone(JTextField textField) {
        Pattern pattern = Pattern.compile(PHONE_PATTERN);
        if (!pattern.matcher(textField.getText()).matches()) {
            return false;
        }
        return true;
    }

    /**
     * Validates if the given string is not null and not empty.
     * @param textField input the string to validate
     * @param errorMessage the error message to set if the validation fails
     * @return true if the string is not null and not empty, false otherwise
     */
    public static int isTextFieldEmpty(JTextField textField, String errorMessage) {
        if (textField.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, errorMessage, "ERROR", JOptionPane.ERROR_MESSAGE);
            return 1;
        }
        return 0;
    }



